// dao/lesson_dao.dartimport 'package:floor/floor.dart';import '../../domain/models/type_model.dart';@daoabstract class MyDao {  // TypeData  @Query('SELECT * FROM TypeData')  Future<List<TypeData>> getAllTypeData();  @Insert(onConflict: OnConflictStrategy.replace)  Future<void> insertTypeData(List<TypeData> data);  @Query("DELETE FROM TypeData")    Future<void> deleteTypeData();}