import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class CustomAppBar extends StatelessWidget {  final String titleName;  const CustomAppBar({super.key, required this.titleName});  @override  Widget build(BuildContext context) {    return Card(      elevation: 2.0, // cardElevation      shape: RoundedRectangleBorder(        borderRadius: BorderRadius.circular(0.0),      ),      child: Container(        height: kToolbarHeight,        color: Color(0xFF0A6F9B),        child: Row(          children: [            InkWell(              onTap: () {                Navigator.of(context).pop();              },              child: Container(                padding: EdgeInsets.all(16.0),                child: Icon(Icons.arrow_back_ios_new, color: Colors.white),              ),            ),            Expanded(              child: SingleChildScrollView(                padding: EdgeInsets.only(right: 16.0),                scrollDirection: Axis.horizontal,                child: Text(                  titleName,                  style: TextStyle(                    fontFamily: 'Montserrat',                    color: Colors.white,                    fontSize: 16.0,                    fontWeight: FontWeight.w600,                  ),                )),            ),            // Add other widgets as needed          ],        ),      ),    );  }}